package week1.day1;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int im =10;
       long fact =1;
       
       for (int i=1; i<=im; i++)
       {
    	   fact = fact*i ;
    	   System.out.println("The factorial of i is" + fact);
       }
    	   
	}

}
